mapper.select:
SELECT USER_SEQ, PRO_SCHOOL, PRO_MAJOR, PRO_TECH, PRO_CAREER, PRO_INFO, PRO_STAR, CAREER_COMPANY, CAREER_DEPT, CAREER_JOB, CAREER_TERM
	FROM CAREER  NATURAL JOIN PROFILE 
		WHERE USER_SEQ =#{user_seq}

mapper.update:
UPDATE PROFILE
SET PRO_SCHOOL=#{pro_school}, PRO_MAJOR=#{pro_major}, 
    PRO_TECH=#{pro_tech}, PRO_INFO=#{pro_info}
WHERE USER_SEQ=#{user_seq}

UPDATE CAREER
SET CAREER_COMPANY=#{career_company}, CAREER_DEPT=#{career_dept},
	CAREER_JOB=#{career_job}, CAREER_TERM=#{career_term}
WHERE USER_SEQ=#{user_seq}
